AOS.init();const burger=document.querySelector('.gn-trig');const menuSlider=()=>{burger.addEventListener('click',()=>{let menuOpen=burger.dataset.open;burger.dataset.open=1;if(menuOpen==0){burger.classList.add('is-active');let navTlIn=gsap.timeline();navTlIn.to('.js-menu-bg',{opacity:1,x:0,duration:0.45});navTlIn.to('.js-menu-item',{opacity:1,x:0,duration:0.45,stagger:0.1,onComplete:function(){burger.dataset.open=2}})}else if(menuOpen==2){let navTlOut=gsap.timeline();navTlOut.to('.js-menu-item',{opacity:0,x:90,duration:0.45,stagger:{from:'end',each:0.1},});navTlOut.to('.js-menu-bg',{opacity:0,x:'100%',duration:0.45,onComplete:function(){burger.dataset.open=0;burger.classList.remove('is-active')}})}})}
menuSlider();function showMenu(){if(window.innerWidth>=768){gsap.set(['.js-menu-bg','.js-menu-item'],{opacity:1,x:0})}else{gsap.set(['.js-menu-bg','.js-menu-item'],{opacity:0,x:'100%'});burger.dataset.open=0;burger.classList.remove('is-active')}}
showMenu();window.addEventListener('resize',showMenu);$(document).ready(function(){$('a[href*=#]').bind('click',function(e){e.preventDefault();var target=$(this).attr("href");$('html, body').stop().animate({scrollTop:$(target).offset().top},600,function(){location.hash=target});return!1})})